function get_hash(p::Char, x::Integer, y::Integer)::UInt64
    k = x + 8*(y-1)
    return @match p begin
        'P' => WPAWN_HASH[k]
        'p' => BPAWN_HASH[k]
        'N' => WKNIGHT_HASH[k]
        'n' => BKNIGHT_HASH[k]
        'B' => WBISHOP_HASH[k]
        'b' => BBISHOP_HASH[k]
        'R' => WROOK_HASH[k]
        'r' => BROOK_HASH[k]
        'Q' => WQUEEN_HASH[k]
        'q' => BQUEEN_HASH[k]
        'K' => WKING_HASH[k]
        'k' => BKING_HASH[k]
        'D' => DUCK_HASH[k]
         _  => UInt64(0)
    end    
end

function get_hash(pos::Position)::UInt64
    hash = UInt64(0)
    for y in 1:8, x in 1:8
        hash = xor(hash, get_hash(pos.board[x,y], x, y))
    end 
    if pos.toplay == -1
        hash = xor(hash, BLACK_TOMOVE_HASH)
    end
    if pos.epx != 0
        hash = xor(hash, EPX_HASH[pos.epx])
    end
    if pos.wck
        hash = xor(hash, WCK_HASH)
    end
    if pos.wcq
        hash = xor(hash, WCQ_HASH)
    end
    if pos.bck
        hash = xor(hash, BCK_HASH)
    end
    if pos.bcq
        hash = xor(hash, BCQ_HASH)
    end
    return hash
end